import React, {useState, useEffect} from 'react';


const Produtos = () => {
    const [produtos, setProdutos] = useState([]);

    useEffect(async() => {
        const result = await fetch("http://localhost:8080/React6/fse/src/php/pedidossapi.php");
        setProdutos(await result.json())
    }, [])

    return (
        <>
            <div className="container-fluid">
                <div className="row">
                    {   
                        pedidos.map(value => {
                            return (
                                
                                <div className="col-lg-12">
                                    <div key={value.id}>
                                        <h5>Cliente: {value.nome_cliente}</h5>
                                        <h5>{value.nome_produto}</h5>
                                        <h5>Valor Unitario: {value.valor_unitario}</h5>
                                        <h5>Qtd: {value.quantidade}</h5>
                                        <h5>Valor total: {value.valor_total}</h5>
                                        <br /> 
                                    </div>
                                </div>
                            )
                        })     
                    }
                </div>
            </div>
        </>
    )
}

export default Pedidos;
